%r
# for loop to estimate returns (as a single column) based on price: P(i)/P(i-1)
returns <- vector();
for(i in 2:length(market_data[,1]))
{
  if(identical(market_data[i,1], market_data[i-1,1]))
  {
    returns[i]=market_data[i,72]/market_data[i-1,72];
  }
  else {
    returns[i]=0;
  }
}

returns[1]=0;

# adding the returns column to the market_data
market_data <- cbind(market_data,returns)

head(market_data, 2)
t(names(market_data))

%r
# for loop to estimate log of returns (as a single column) based on price: Pt/P(t-1)
logreturns <- vector();
for(i in 2:length(market_data.factors[,1]))
  {
    logreturns[i]=log(market_data.factors[i,23]);
}

head(logreturns)

# adding the logreturns column to the market_data
market_data.factors <- cbind(market_data.factors,logreturns)

head(market_data.factors, 2)
t(names(market_data.factors))
